def dividir_em_numeros_de_seis_digitos(s):
    # Lista para armazenar os números resultantes
    numeros = []

    # Divide a string em pedaços de 6 caracteres
    for i in range(0, len(s), 6):
        pedaco = s[i:i + 6]

        # Converte o pedaço para um número e adiciona à lista
        numeros.append(int(pedaco))

    return numeros


# Exemplo de uso
string_grande = ""
numeros = dividir_em_numeros_de_seis_digitos(string_grande)
print(numeros)

def buscar_numero(numero, lista_numeros):
    return numero in lista_numeros


# Exemplo de busca
numero_a_buscar = 123456
existe = buscar_numero(numero_a_buscar, numeros)
print(f'O número {numero_a_buscar} {"está" if existe else "não está"} presente na lista.')
